import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserData } from '../context/UserDataContext';
import { 
  validateName, 
  validatePhoneNumber, 
  validateSymptoms, 
  validateLocation, 
  validateZipCode, 
  validateInsurance,
  validateAppointmentDate,
  validateAppointmentTime,
  sanitizeInput 
} from '../utils/validation';

const UserIntakeForm = () => {
  const navigate = useNavigate();
  const { state, dispatch } = useUserData();
  const [formData, setFormData] = useState({
    name: state.userData.name || '',
    phoneNumber: state.userData.phoneNumber || '',
    primarySymptoms: state.userData.primarySymptoms || '',
    address: state.userData.address || '',
    city: state.userData.city || '',
    zipCode: state.userData.zipCode || '',
    insuranceProvider: state.userData.insuranceProvider || '',
    insurancePlan: state.userData.insurancePlan || '',
    preferredDate: state.userData.preferredDate || '',
    preferredTime: state.userData.preferredTime || ''
  });
  const [errors, setErrors] = useState({});

  // Check if user is authenticated
  useEffect(() => {
    if (!state.user) {
      navigate('/login');
    }
  }, [state.user, navigate]);

  const insuranceOptions = [
    { value: '', label: 'Select your insurance provider' },
    { value: 'Aetna', label: 'Aetna' },
    { value: 'Blue Cross', label: 'Blue Cross' },
    { value: 'Cigna', label: 'Cigna' },
    { value: 'UnitedHealth', label: 'UnitedHealth' }
  ];

  const insurancePlans = {
    'Aetna': [
      { value: '', label: 'Select plan' },
      { value: 'Silver PPO', label: 'Silver PPO' },
      { value: 'Gold PPO', label: 'Gold PPO' },
      { value: 'Bronze PPO', label: 'Bronze PPO' }
    ],
    'Blue Cross': [
      { value: '', label: 'Select plan' },
      { value: 'Gold HMO', label: 'Gold HMO' },
      { value: 'Silver HMO', label: 'Silver HMO' },
      { value: 'Platinum PPO', label: 'Platinum PPO' }
    ],
    'Cigna': [
      { value: '', label: 'Select plan' },
      { value: 'Bronze PPO', label: 'Bronze PPO' },
      { value: 'Silver PPO', label: 'Silver PPO' },
      { value: 'Gold PPO', label: 'Gold PPO' }
    ],
    'UnitedHealth': [
      { value: '', label: 'Select plan' },
      { value: 'Platinum PPO', label: 'Platinum PPO' },
      { value: 'Gold PPO', label: 'Gold PPO' },
      { value: 'Silver PPO', label: 'Silver PPO' }
    ]
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const sanitizedValue = sanitizeInput(value);
    
    setFormData(prev => ({
      ...prev,
      [name]: sanitizedValue
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    const nameValidation = validateName(formData.name);
    if (!nameValidation.isValid) {
      newErrors.name = nameValidation.error;
    }
    
    const phoneValidation = validatePhoneNumber(formData.phoneNumber);
    if (!phoneValidation.isValid) {
      newErrors.phoneNumber = phoneValidation.error;
    }
    
    const symptomsValidation = validateSymptoms(formData.primarySymptoms);
    if (!symptomsValidation.isValid) {
      newErrors.primarySymptoms = symptomsValidation.error;
    }
    
    const addressValidation = validateLocation(formData.address);
    if (!addressValidation.isValid) {
      newErrors.address = addressValidation.error;
    }
    
    const cityValidation = validateLocation(formData.city);
    if (!cityValidation.isValid) {
      newErrors.city = cityValidation.error;
    }
    
    const zipValidation = validateZipCode(formData.zipCode);
    if (!zipValidation.isValid) {
      newErrors.zipCode = zipValidation.error;
    }
    
    const insuranceValidation = validateInsurance(formData.insuranceProvider);
    if (!insuranceValidation.isValid) {
      newErrors.insuranceProvider = insuranceValidation.error;
    }
    
    if (formData.insuranceProvider && !formData.insurancePlan) {
      newErrors.insurancePlan = 'Please select your insurance plan';
    }
    
    const dateValidation = validateAppointmentDate(formData.preferredDate);
    if (!dateValidation.isValid) {
      newErrors.preferredDate = dateValidation.error;
    }
    
    const timeValidation = validateAppointmentTime(formData.preferredTime);
    if (!timeValidation.isValid) {
      newErrors.preferredTime = timeValidation.error;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      dispatch({
        type: 'SET_USER_DATA',
        payload: formData
      });
      navigate('/results');
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 style={{ marginBottom: '30px', color: '#333' }}>Tell Us About Your Health Needs</h1>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="symptoms">What symptoms are you experiencing?</label>
            <textarea
              id="symptoms"
              name="symptoms"
              value={formData.symptoms}
              onChange={handleInputChange}
              placeholder="Describe your symptoms in detail (e.g., skin rash, chest pain, headache)"
              rows="4"
              className={errors.symptoms ? 'error' : ''}
            />
            {errors.symptoms && <div className="error" style={{ marginTop: '8px' }}>{errors.symptoms}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="location">Where are you located?</label>
            <input
              type="text"
              id="location"
              name="location"
              value={formData.location}
              onChange={handleInputChange}
              placeholder="Enter your city or zip code"
              className={errors.location ? 'error' : ''}
            />
            {errors.location && <div className="error" style={{ marginTop: '8px' }}>{errors.location}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="insurance">What's your insurance provider?</label>
            <select
              id="insurance"
              name="insurance"
              value={formData.insurance}
              onChange={handleInputChange}
              className={errors.insurance ? 'error' : ''}
            >
              {insuranceOptions.map(option => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            {errors.insurance && <div className="error" style={{ marginTop: '8px' }}>{errors.insurance}</div>}
          </div>

          <div style={{ display: 'flex', gap: '16px', marginTop: '30px' }}>
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => navigate('/')}
            >
              Back
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              style={{ flex: '1' }}
            >
              Find Providers
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UserIntakeForm;
